import {  LOG_IN } from "./../actionTypes";
const initialState = {
  userDetails: [],
};

export default function details( state = initialState , action) {
  switch (action.type) {
      case ADD_DETAILS: {
          return {
              ...state,
              userDetails: [...state.userDetails, action.payload],
            };
        }
        default: {
            return state;
        }
  }
}
